DROP TABLE IF EXISTS `desgloce_evaluacion_grupos`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `desgloce_evaluacion_grupos` (
  `id` int NOT NULL AUTO_INCREMENT,
  `observaciones` varchar(500) DEFAULT NULL,
  `grupo` int NOT NULL,
  `obtenido` float DEFAULT NULL,
  `evaluacion` int NOT NULL,
  PRIMARY KEY (`id`),
  KEY `fk_desgoce_evaulacion_grupos_catalogo_grupos1_idx` (`grupo`),
  KEY `fk_desgoce_evaulacion_grupos_evaluacion1_idx` (`evaluacion`),
