  CONSTRAINT `fk_desgoce_evaulacion_grupos_evaluacion1` FOREIGN KEY (`evaluacion`) REFERENCES `evaluacion` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=443 DEFAULT CHARSET=utf8mb3;
