--
-- Table structure for table `evaluacion`
--

DROP TABLE IF EXISTS `evaluacion`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `evaluacion` (
  `id` int NOT NULL AUTO_INCREMENT,
  `evaluador` int unsigned NOT NULL,
  `proyecto` int NOT NULL,
  `calificacion` int DEFAULT NULL,
  `recomendacion` varchar(1000) DEFAULT NULL,
  `observaciones` varchar(1000) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL,
  `aprobado` enum('SI','NO') DEFAULT NULL,
  `evaluado` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`,`evaluador`,`proyecto`),
  KEY `fk_evaluacion_users1_idx` (`evaluador`),
  KEY `fk_evaluacion_proyecto1_idx` (`proyecto`),
  CONSTRAINT `fk_evaluacion_proyecto1` FOREIGN KEY (`proyecto`) REFERENCES `proyecto` (`id`),
  CONSTRAINT `fk_evaluacion_users1` FOREIGN KEY (`evaluador`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=161 DEFAULT CHARSET=utf8mb3;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `evaluacion`
